const fs    =      require('fs');
const path  =    require('path');

var jsFiles = ['main.js']; var cssFiles = ['reset.css','main.css','burger.css'];

const jsBLZPath = path.join(__dirname, 'js/vendor/blz');
const jsDistPath = path.join(__dirname, 'dist/js');
const cssBLZPath = path.join(__dirname, 'css/vendor/blz');
const cssDistPath = path.join(__dirname, 'dist/css');

fs.writeFile(path.join(cssDistPath, 'blz.css'), "", function (err) {
    if (err) throw err;
    console.log('CSS Distribution File was created successfully.');
});
fs.writeFile(path.join(jsDistPath, 'blz.js'), "", function (err) {
    if (err) throw err;
    console.log('JS Distribution File was created successfully.');
});

cssFiles.forEach(function(file){
    fs.readFile(path.join(cssBLZPath, file), 'utf8', function (err,data) {
        if (err) { return console.log(err); }
        fs.appendFile(path.join(cssDistPath, 'blz.css'), data, function (err) {
            if (err) throw err;
            console.log('Successfully added CSS module: ' + file);
        });
    });
});
jsFiles.forEach(function(file){
    fs.readFile(path.join(jsBLZPath, file), 'utf8', function (err,data) {
        if (err) { return console.log(err); }
        fs.appendFile(path.join(jsDistPath, 'blz.js'), data, function (err) {
            if (err) throw err;
            console.log('Successfully added JS module: ' + file);
        });
    });
});

const options = {
    html: {
        removeAttributeQuotes: false,
        removeOptionalTags: false,
    },
};
